import React from 'react';

class ProgressBar extends React.Component {
  percentNumber = () => {
    let temp = 0;
    const { todoItems } = this.state;
    todoItems.map(item => {
      if (item.isCompleted) {
        temp++;
      }
      console.log(temp);
      return temp;
    });
    if (temp > 0) {
      this.setState({
        progressBar: ((temp / todoItems.length) * 100).toPrecision(3)
      });
    }
  };
  render() {
    const { progressBar } = this.props;
    console.log(progressBar);
    return (
      <div className="Wrapper-progress">
        <span className="number-percent">{progressBar}%</span>
        <div className="block-percent" />
      </div>
    );
  }
}

export default ProgressBar;
