import React from 'react';
import './App.css';
import TodoItem from './components/TodoItem';
import checkAll from './images/list.svg';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      todoItems: [
        { title: 'Get to work', isCompleted: false },
        { title: 'Go home', isCompleted: false },
        { title: 'Self study at home', isCompleted: false }
      ]
    };
  }

  onItemClicked = item => {
    console.log(item);
    const { todoItems } = this.state;
    return () => {
      this.setState({
        todoItems: todoItems.map(i =>
          i === item ? { ...i, isCompleted: !item.isCompleted } : { ...i }
        )
      });
    };
  };

  render() {
    const { todoItems } = this.state;
    return (
      <div className="App">
        <div className="Header">
          <img src={checkAll} alt="" />
          <input type="text" placeholder="What needs to be done?" />
        </div>
        {todoItems.length &&
          todoItems.map((item, index) => (
            <TodoItem
              key={index}
              item={item}
              onClick={this.onItemClicked(item)}
            />
          ))}
      </div>
    );
  }
}

export default App;
