import React from 'react';
import './App.css';
import TodoItem from './components/TodoItem';
import checkAll from './images/list.svg';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      todoItems: [
        { title: 'Get to work', isCompleted: false },
        { title: 'Go home', isCompleted: false },
        { title: 'Self study at home', isCompleted: false }
      ],
      newItem: ''
    };
  }

  onItemClicked = item => {
    console.log(item.isCompleted);
    const { todoItems } = this.state;
    return () => {
      this.setState({
        todoItems: todoItems.map(i =>
          i === item ? { ...i, isCompleted: !item.isCompleted } : { ...i }
        )
      });
    };
  };

  onItemCheckAll = () => {
    this.setState({
      todoItems: [
        {
          title: '',
          isCompleted: true,
          ...this.state.todoItems
        }
      ]
    });
  };

  onKeyDown = e => {
    if (e.keyCode === 13) {
      let text = e.target.value;
      if (!text) {
        return;
      }
      text = text.trim();
      if (!text) {
        return;
      }
      this.setState({
        todoItems: [
          { title: text, isCompleted: false },
          ...this.state.todoItems
        ],
        newItem: ''
      });
    }
  };

  onChange = e => {
    this.setState({
      newItem: e.target.value
    });
  };

  render() {
    const { todoItems, newItem } = this.state;
    return (
      <div className="App">
        <div className="Header">
          <img src={checkAll} alt="" onClick={this.onItemCheckAll} />
          <input
            type="text"
            value={newItem}
            onChange={this.onChange}
            placeholder="What needs to be done?"
            onKeyDown={this.onKeyDown}
          />
        </div>
        {todoItems.length &&
          todoItems.map((item, index) => (
            <TodoItem
              key={index}
              item={item}
              onClick={this.onItemClicked(item)}
            />
          ))}
      </div>
    );
  }
}

export default App;
