import React from 'react';
import './App.css';
import TodoItem from './components/TodoItem';

class App extends React.Component {
  constructor() {
    super();
    this.state = {
      todoItems: [
        { title: 'Get to work', isCompleted: false },
        { title: 'Go home', isCompleted: false },
        { title: 'Self study at home', isCompleted: false }
      ]
    };
  }

  onItemClicked(item) {
    const { todoItems } = this.state;
    return () => {
      this.setState({
        todoItems: todoItems.map(i =>
          i !== item ? { ...i } : { ...i, isCompleted: !item.isCompleted }
        )
      });
    };
  }

  render() {
    const { todoItems } = this.state;
    if (todoItems.length) {
      return (
        <div className="App">
          {todoItems.length &&
            todoItems.map((item, index) => (
              <TodoItem
                key={index}
                item={item}
                onClick={this.state.onItemClicked}
              />
            ))}
        </div>
      );
    }
  }
}

export default App;
